        process_4_finish <= 0;

        if (ongoing_process_number === process_num && process_busy === 0 ) begin
            get_ack = 1;
            if (write_outbuf_addr_run_flag === 1 && get_ack === 1) begin
                process_busy = 1;
                //write outbuf_addr data 
                for (i = 0 ; i < four_byte_num ; i = i+1) begin
                    if (outbuf_addr_c_bitwidth < 32) begin
                        outbuf_addr_data_tmp_reg = mem_outbuf_addr[write_outbuf_addr_count];
                    end
                    else begin
                        for (j=0 ; j<32 ; j = j + 1) begin
                            if (i*32 + j < outbuf_addr_c_bitwidth) begin
                                outbuf_addr_data_tmp_reg[j] = mem_outbuf_addr[write_outbuf_addr_count][i*32 + j];
                            end
                            else begin
                                outbuf_addr_data_tmp_reg[j] = 0;
                            end
                        end
                    end
                    write (outbuf_addr_data_in_addr + write_outbuf_addr_count * four_byte_num * 4 + i * 4, outbuf_addr_data_tmp_reg, write_outbuf_addr_resp);
                end
                process_busy = 0;
                write_one_outbuf_addr_data_done <= 1;
                @(posedge clk);
                write_one_outbuf_addr_data_done <= 0;
            end   
            process_4_finish <= 1;
        end
        @(posedge clk);
    end    
end

always @(reset or posedge clk) begin
    if (reset == 0) begin
        write_start_run_flag <= 0; 
        write_start_count <= 0;
    end
    else begin
        if (write_start_count >= 8) begin
            write_start_run_flag <= 0; 
        end
        else if (TRAN_AXILiteS_write_start_in === 1) begin
            write_start_run_flag <= 1; 
        end
        if (AESL_write_start_finish === 1) begin
            write_start_count <= write_start_count + 1;
            write_start_run_flag <= 0; 
        end
    end
end

initial begin : write_start
    reg [DATA_WIDTH - 1 : 0] write_start_tmp;
    integer process_num;
    integer write_start_resp;
    wait(reset === 1);
    @(posedge clk);
    process_num = 5;
    while (1) begin
        process_5_finish = 0;
        if (ongoing_process_number === process_num && process_busy === 0 ) begin
            if (write_start_run_flag === 1) begin
                process_busy = 1;
                write_start_tmp=0;
                write_start_tmp[0 : 0] = 1;
                write (START_ADDR, write_start_tmp, write_start_resp);
                process_busy = 0;
                AESL_write_start_finish <= 1;
                @(posedge clk);
                AESL_write_start_finish <= 0;
            end
            process_5_finish <= 1;
        end 
        @(posedge clk);
    end
end

always @(reset or posedge clk) begin
    if (reset == 0) begin
        memptr_read_data_finish <= 0;
        read_memptr_run_flag <= 0; 
        read_memptr_count = 0;
    end
    else begin
        if (AESL_done_index_reg === 1) begin
            read_memptr_run_flag = 1; 
        end
        if (TRAN_AXILiteS_transaction_done_in === 1) begin
            memptr_read_data_finish <= 0;
            read_memptr_count = 0; 
        end
        if (read_one_memptr_data_done === 1) begin
            read_memptr_count = read_memptr_count + 1;
            if (read_memptr_count == memptr_OPERATE_DEPTH) begin
                read_memptr_run_flag <= 0; 
                memptr_read_data_finish <= 1;
            end
        end
    end
end

initial begin : read_memptr
    integer read_memptr_resp;
    integer process_num;
    integer get_vld;
    integer four_byte_num;
    integer c_bitwidth;
    integer i;
    integer j;

    wait(reset === 1);
    @(posedge clk);
    c_bitwidth = memptr_c_bitwidth;
    process_num = 6;
    count_c_data_four_byte_num_by_bitwidth (c_bitwidth , four_byte_num) ;
    while (1) begin
        process_6_finish <= 0;
        if (ongoing_process_number === process_num && process_busy === 0 ) begin
            if (read_memptr_run_flag === 1) begin
                process_busy = 1;
                get_vld = 1;
                if (get_vld == 1) begin
                    //read memptr data 
                    for (i = 0 ; i < four_byte_num ; i = i+1) begin
                        read (memptr_data_out_addr + read_memptr_count * four_byte_num * 4 + i * 4, RDATA_reg, read_memptr_resp);
                        if (memptr_c_bitwidth < 32) begin
                            mem_memptr[read_memptr_count] <= RDATA_reg;
                        end
                        else begin
                            for (j=0 ; j < 32 ; j = j + 1) begin
                                if (i*32 + j < memptr_c_bitwidth) begin
                                    mem_memptr[read_memptr_count][i*32 + j] <= RDATA_reg[j];
                                end
                            end
                        end
                    end
                    
                    read_one_memptr_data_done <= 1;
                    @(posedge clk);
                    read_one_memptr_data_done <= 0;
                end    
                process_busy = 0;
            end    
            process_6_finish <= 1;
        end
        @(posedge clk);
    end    
end
//------------------------Task and function-------------- 
task read_token; 
    input integer fp; 
    output reg [127 : 0] token;
    integer ret;
    begin
        token = "";
        ret = 0;
        ret = $fscanf(fp,"%s",token);
    end 
endtask 
 
//------------------------Read file------------------------ 
 
// Read data from file 
initial begin : read_memptr_file_process 
  integer fp; 
  integer ret; 
  integer factor; 
  reg [127 : 0] token; 
  reg [memptr_c_bitwidth - 1 : 0] token_tmp; 
  reg [DATA_WIDTH - 1 : 0] mem_tmp; 
  reg [ 8*5 : 1] str;
  integer transaction_idx; 
  integer i; 
  transaction_idx = 0; 
  mem_tmp [DATA_WIDTH - 1 : 0] = 0;
  count_seperate_factor_by_bitwidth (memptr_c_bitwidth , factor);
  fp = $fopen(`TV_IN_memptr ,"r"); 
  if(fp == 0) begin                               // Failed to open file 
      $display("Failed to open file \"%s\"!", `TV_IN_memptr); 
      $finish; 
  end 
  read_token(fp, token); 
  if (token != "[[[runtime]]]") begin             // Illegal format 
      $display("ERROR: Simulation using HLS TB failed.");
      $finish; 
  end 
  read_token(fp, token); 
  while (token != "[[[/runtime]]]") begin 
      if (token != "[[transaction]]") begin 
          $display("ERROR: Simulation using HLS TB failed.");
          $finish; 
      end 
      read_token(fp, token);                        // skip transaction number 
      @(posedge clk);
      # 0.2;
      while(AESL_ready_reg !== 1) begin
          @(posedge clk); 
          # 0.2;
      end
      for(i = 0; i < memptr_DEPTH; i = i + 1) begin 
          read_token(fp, token); 
          ret = $sscanf(token, "0x%x", token_tmp); 
          if (factor == 4) begin
              if (i%factor == 0) begin
                  mem_tmp [7 : 0] = token_tmp;
              end
              if (i%factor == 1) begin
                  mem_tmp [15 : 8] = token_tmp;
              end
              if (i%factor == 2) begin
                  mem_tmp [23 : 16] = token_tmp;
              end
              if (i%factor == 3) begin
                  mem_tmp [31 : 24] = token_tmp;
                  mem_memptr [i/factor] = mem_tmp;
                  mem_tmp [DATA_WIDTH - 1 : 0] = 0;
              end
          end
          if (factor == 2) begin
              if (i%factor == 0) begin
                  mem_tmp [15 : 0] = token_tmp;
              end
              if (i%factor == 1) begin
                  mem_tmp [31 : 16] = token_tmp;
                  mem_memptr [i/factor] = mem_tmp;
                  mem_tmp [DATA_WIDTH - 1: 0] = 0;
              end
          end
          if (factor == 1) begin
              mem_memptr [i] = token_tmp;
          end
      end 
      if (factor == 4) begin
          if (i%factor != 0) begin
              mem_memptr [i/factor] = mem_tmp;
          end
      end
      if (factor == 2) begin
          if (i%factor != 0) begin
              mem_memptr [i/factor] = mem_tmp;
          end
      end 
      read_token(fp, token); 
      if(token != "[[/transaction]]") begin 
          $display("ERROR: Simulation using HLS TB failed.");
          $finish; 
      end 
      read_token(fp, token); 
      transaction_idx = transaction_idx + 1; 
  end 
  $fclose(fp); 
end 
 
//------------------------Write file----------------------- 
 
// Write data to file 
 
initial begin : write_memptr_file_proc 
  integer fp; 
  integer factor; 
  integer transaction_idx; 
  reg [memptr_c_bitwidth - 1 : 0] mem_tmp; 
  reg [ 100*8 : 1] str;
  integer i; 
  transaction_idx = 0; 
  count_seperate_factor_by_bitwidth (memptr_c_bitwidth , factor);
  while(1) begin 
      @(posedge clk);
      while (memptr_read_data_finish !== 1) begin
          @(posedge clk);
      end
      # 0.1;
      fp = $fopen(`TV_OUT_memptr, "a"); 
      if(fp == 0) begin       // Failed to open file 
          $display("Failed to open file \"%s\"!", `TV_OUT_memptr); 
          $finish; 
      end 
      $fdisplay(fp, "[[transaction]] %d", transaction_idx);
      for (i = 0; i < (memptr_DEPTH - memptr_DEPTH % factor); i = i + 1) begin
          if (factor == 4) begin
              if (i%factor == 0) begin
                  mem_tmp = mem_memptr[i/factor][7:0];
              end
              if (i%factor == 1) begin
                  mem_tmp = mem_memptr[i/factor][15:8];
              end
              if (i%factor == 2) begin
                  mem_tmp = mem_memptr[i/factor][23:16];
              end
              if (i%factor == 3) begin
                  mem_tmp = mem_memptr[i/factor][31:24];
              end
              $fdisplay(fp,"0x%x",mem_tmp);
          end
          if (factor == 2) begin
              if (i%factor == 0) begin
                  mem_tmp = mem_memptr[i/factor][15:0];
              end
              if (i%factor == 1) begin
                  mem_tmp = mem_memptr[i/factor][31:16];
              end
              $fdisplay(fp,"0x%x",mem_tmp);
          end
          if (factor == 1) begin
              $fdisplay(fp,"0x%x",mem_memptr[i]);
          end
      end 
      if (factor == 4) begin
          if ((memptr_DEPTH - 1) % factor == 2) begin
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][7:0]);
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][15:8]);
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][23:16]);
          end
          if ((memptr_DEPTH - 1) % factor == 1) begin
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][7:0]);
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][15:8]);
          end
          if ((memptr_DEPTH - 1) % factor == 0) begin
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][7:0]);
          end
      end
      if (factor == 2) begin
          if ((memptr_DEPTH - 1) % factor == 0) begin
              $fdisplay(fp,"0x%x",mem_memptr[memptr_DEPTH / factor][15:0]);
          end
      end
      $fdisplay(fp, "[[/transaction]]");
      transaction_idx = transaction_idx + 1;
      $fclose(fp); 
      while (TRAN_AXILiteS_start_in !== 1) begin
          @(posedge clk);
      end
  end 
end 
 
